import React, { useState } from 'react';
import axios from 'axios';

function TrainSearch() {
  const [source, setSource] = useState('');
  const [destination, setDestination] = useState('');
  const [date, setDate] = useState('');
  const [trains, setTrains] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.get('http://localhost:8000/api/seat-availabilities/check_availability/', {
        params: { source, destination, date }
      });
      setTrains(response.data);
    } catch (error) {
      console.error('Search failed:', error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="text" value={source} onChange={(e) => setSource(e.target.value)} placeholder="Source" />
        <input type="text" value={destination} onChange={(e) => setDestination(e.target.value)} placeholder="Destination" />
        <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
        <button type="submit">Search</button>
      </form>
      <ul>
        {trains.map(train => (
          <li key={train.train}>
            {train.train_name} - Available seats: {train.available_seats}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TrainSearch;
